import os
from fynd_logger import FyndLogger

class FyndException(BaseException):
    def __init__(self,exceptionType,traceback):
        if isinstance(traceback, traceback):
            FyndLogger.error({"message":exceptionType,"traceback":traceback.format_tb()})

class FyndCuctsomException(FyndException):
    def __init__(self,exceptionType,traceback):
        super().__init__(exceptionType,traceback)

class NumericalException(FyndException):
    def __init__(self):
        super().__init__("Numeric")